(function($) {
	$.addPage = function(f, p) {
		var p = $.extend($.fn.pfwpage.defaults, p);
		_f = $(f);
		var t = _f.find(p.page_table);
		t.addClass("page_table");
		addCol = function(page_col) {
			if (page_col) {
				_c = $('<tr/>');
				$.map(page_col, function(n, i) {
					if(n.width)
					{
						_bth = $('<th width="'+n.width+'">' + n.col_name + '</th>');
					}else{
						_bth = $('<th>' + n.col_name + '</th>');
					}
					
					if(n.height)
					{
						_c.attr("height",n.height);
					}
					
					if (n.sort) {
						_bth.addClass("page_table_th_sort").click(function(e) {
							if (n.sort) {
								sort(n.property, 'asc');
							}
						});
					}
					_c.append(_bth);
				});
				_ch = $('<thead/>').append(_c);
				t.prepend(_ch);
			}
		};
		addCol(p.page_col);
		t.find("tr").each(function() {
			$(this).find("td").eq(0).each(function() {
				$(this).click(function() {
					var t1 = $(this).parent().parent().parent().attr("class");
					if(t1 != "undefined" && t1 == "treeTable page_table")
					{
						$(this).parent().toggleClass("page_tr_selected");
					}
				});
			});
		});
		t.find("tr").each(function() {
			$(this).click(function(e) {
				$(this).toggleClass("page_tr_selected");
			})
		});
		
		/*
		t.find("span[class=expander]").each(function() {
			$(this).click(function(e) {
				alert($(this).parent().parent().attr("class"));
			})
		});
		*/
				
		onSearch = function(e) {
			$(p.page_table_search).toggle();
		};
		selectall = function(e) {
	 		$("tr[title]").each(function(){
	 			$(this).addClass("page_tr_selected");
	 		});
		};
		selectnone = function(e) {
	 		$("tr[title]").each(function(){
	 			$(this).removeClass("page_tr_selected");
	 		});		
		};
		reLoad = function(e) {
			_f.submit();
		};
		onButton = function(url) {
			_f.attr('action', url);
			_f.submit();
		};
		getSelectData = function() {
			var d = [];
			$(".page_tr_selected", _f).each(function() {
				if (this.title != "" && typeof (this.title) != "undefined") {
					d[d.length] = this.title;
				}
			});
			return d;
		};
		_show = function(c, t) {
			var pop = new Popup( {
				contentType : 4,
				isReloadOnClose : false,
				width : 340,
				height : 80
			});
			pop.setContent("title", t ? t : "提示");
			pop.setContent("alertCon", c ? c : "对话框的内容");
			pop.build();
			pop.show();
		};
		buttonInfo = function(_url, _par, _stag, _extn, _extv, _extn1, _extv1,
				_cfstr,_interbefore,_print,_expt) {

			if (_par) {
				_sd = getSelectData();
				if (_stag) {
					if (!_sd || _sd.length < 0) {
						_show("请选择一条需要操作的数据！", '提示');
						return;
					} else {
						var reg = new RegExp(_stag);
						if (reg.test(_sd.length)) {
							if (_cfstr) {
								if (!confirm(_cfstr))
									return;
							}
							if(typeof(_par) != "undefined")
							{
								if(_par == "id")
								{
									_url += "?" + _par + "=" + _sd[0];
									
									if (_extn && _extv) {
										_url += "&" + _extn + "=" + _extv;
									}
									if (_extn1 && _extv1) {
										_url += "&" + _extn1 + "=" + _extv1;
									}
							
							}else if( _par == "checks" ){
								var tmpStr = "";
								for( var vp = 0; vp < _sd.length; vp++)
								{
									tmpStr += _sd[vp] + ",";
								}
								if(tmpStr != "")
								{
									tmpStr = tmpStr.substring(0,tmpStr.length-1);
								}
								//_url += "?" + _par + "=" + _sd[0];
								//for ( var vp = 1; vp < _sd.length; vp++) {
								//	_url += "&" + _par + "=" + _sd[vp];
								//}
								var tmpObj = _f.find('input[name="hidfy"]');
								tmpObj.val(tmpStr);
								
								if (_extn && _extv) {
									_url += "?" + _extn + "=" + _extv;
								}
								if (_extn1 && _extv1) {
									_url += "&" + _extn1 + "=" + _extv1;
								}
							}else{
								alert("非法传值，非法操作");
								return;
							}
						  }
						} else {
							_show("请选择合法需要操作的数据！", '提示');
							return;
						}
					}
				}
			}
			if(_print){
				var printStr=_print;
				if(typeof(printStr) == "undefined"){
					_show("错误，printFunction函数没有返回值", '提示');
					return;
				}
				if(printStr == "true")
				{
					if(!_url&&_par!="checks"){
						alert("非法传值，非法操作");
						return;
					}else{
						  if (!_sd || _sd.length < 0) {
					           _show("请选择一条需要操作的数据！", '提示');
					           return;
				           }else{
					          var form = $("<form>");  
                              form.attr('style','display:none');  
                              form.attr('target','');
                              form.attr('method','post');  
                              form.attr('action',_url); 
                              
                              $('body').append(form);
                              var input1="";
                              for(var i = 0; i < _sd.length; i++)
							     {
                            	   var input1 = $('<input>');  
                                   input1.attr('type','hidden');  
                                   input1.attr('name',_par);
                                   input1.attr('value',_sd[i]); 
                                   form.append(input1); 
							     }   
                              
                              form.submit(); 
                              form.remove(); 
					          return;
				        }
					}  
				}
				return;
			}
			if(_expt){
				var exptStr=_expt;
				if(typeof(exptStr) == "undefined"){
					_show("错误，printFunction函数没有返回值", '提示');
					return;
				}
				if(exptStr == "true")
				{
			          var form1 = $("<form>");  
                      form1.attr('style','display:none');  
                      form1.attr('target','');
                      form1.attr('method','post');  
                      form1.attr('action',_url); 
                      
                     _f.clone().prependTo(form1);
                      
                      $('body').append(form1);
                      
                      form1.submit(); 
                      form1.remove(); 
                      
			          return;
				}
				return;
			}
			if(_interbefore)
			{
				var jsStr=_interbefore+"()";  
				var returnVal = eval(jsStr);
				if(typeof(returnVal) == "undefined")
				{
					_show("错误，interBefore函数没有返回值", '提示');
					return;
				}
				if(typeof(returnVal) == "boolean")
				{
					if(returnVal != true)
					{
						//_show("interBefore验证不通过", '提示');
						return;
					}
				}else{
					_show(returnVal, '提示');
					return;
				}
			}
			_f.attr("action",_url);
			_f.submit();
		};
		
		_h = $('<div/>');

		if (p.page_button) {
			$
					.map(
							p.page_button,
							function(n, i) {
								var _par = n.paramName;
								var _extn = n.extparamName;
								var _extv = n.extparamValue;
								var _extn1 = n.extparamName1;
								var _extv1 = n.extparamValue1;
								var _cfstr = n.confirmStr;
								var _stag = n.selectNum;
								var _url = n.b_url;
								var _interbefore = n.interFunction;
								var _print=n.printFunction;
								var _disp=n.disp;
								var _expt=n.expt;
								//判断是否显示该按钮
								if(typeof (_disp) != "undefined" && _disp == "0")
								{}else
								{
									_h
											.append($(
													'<input type="button" class="page_table_botton" value="' + n.b_name + '" />')
													.click(
															function(e) {
																if (n.b_function) {
																	n.b_function()
																} else {
																	buttonInfo(
																			_url,
																			_par,
																			_stag,
																			_extn,
																			_extv,
																			_extn1,
																			_extv1,
																			_cfstr,
																			_interbefore,
																			_print,
																			_expt
																			)
																}
															}));
								}

							});
		}
		if (p.page_table_search) {
			$(p.page_table_search).addClass("page_search_table");
			//_find = function(){return $('<input type="button" class="page_table_botton" value="find"/>').click(onSearch);};
			_selAll = function(){return $('<input type="button" class="page_table_botton" value="全选"/>').click(selectall);};
			_selNon = function(){return $('<input type="button" class="page_table_botton" value="重选"/>').click(selectnone);};
			_h.append(_selAll);
			_h.append(_selNon);
		}
		t.before(_h);
		delete _h;
		jumpPage = function(_pageNo) {
			_f.find('input[name=\"' + p.page_pageNo_Name + '\"]').val(_pageNo);
			_f.append('<input type="hidden" name="fyclick" value="Y"/>');
			_f.submit();
		};
		sort = function(orderBy, defaultOrder) {
			_ob = _f.find('input[name="page.orderBy"]');
			_o = _f.find('input[name="page.order"]');
			if (_ob.val() == orderBy) {
				if (_o.val() == "") {
					_o.val(defaultOrder);
				} else if (_o.val() == "desc") {
					_o.val("asc");
				} else if (_o.val() == "asc") {
					_o.val("desc");
				}
			} else {
				_ob.val(orderBy);
				_o.val(defaultOrder);
			}
			_f.submit();
		};
		if (p.page_table != false) {
			_a = $('<div/>').addClass("ui-grid-a");
			_a
					.append(
						'<input type="hidden" name="hidfy"/>')
					.append(
							'<input type="hidden" name="page.orderBy" value="' + p.page_orderBy + '"/>')
					.append(
							'<input type="hidden" name="page.order" id="order" value="' + p.page_order + '"/>')
					.append(
							'<input name="page.pageNo" type="hidden" value="'
									+ p.page_pageNo + '"/>');
			if (p.page_pageNo > 1) {
				_a.append($('<div class="ui-block-a"><a href="#" data-role="button">上一页</a></div>').addClass(
						"page_table_footer_a").click(function(e) {
					jumpPage(p.page_pageNo - 1)
				}));
			}
			if (p.page_pageNo < p.page_totalPages) {
				_a.append($('<div class="ui-block-b"><a href="#" data-role="button">下一页</a></div>').addClass(
						"page_table_footer_a").click(function(e) {
					jumpPage(p.page_pageNo + 1)
				}));
			}
			
			t.after(_a);
			delete _a;
		}
	};
	var docloaded = false;
	$(document).ready(function() {
		docloaded = true
	});
	$.fn.pfwpage = function(p) {
		return this.each(function() {
			if (!docloaded) {
				$(this).hide();
				var t = this;
				$(document).ready(function() {
					$.addPage(t, p);
				});
			} else {
				$.addPage(this, p);
			}
		});
	};
	var pfw_encode_uri = function(text) {
		if (encodeURIComponent)
			return encodeURIComponent(text);
		else
			return escape(text);
	};
	$.fn.pfwpage.defaults = {
		sumbitfunction : null,
		_del_url : null,
		_update_url : null,
		pageable : true,
		page_table : null,
		defaultOrderBy : 'id',
		defaultOrder : 'desc',
		page_orderBy : null,
		page_order : null,
		page_pageNo_Name : 'page.pageNo',
		page_pageNo : 1,
		page_pageSize_Name : 'page.pageSize',
		page_pageSize : 15,
		page_totalPages : 0,
		page_totalCount : 0
	};
})(jQuery);